var weatherData = msg.payload.match(/(\d+)/g) || [""];
msg.maxCelcius = weatherData[0];
msg.minCelcius = weatherData[1];
msg.rainPercent = weatherData[2];

msg.cycles = 36
msg.period = 5

if([6, 7, 8].includes(msg.hour)) {
    msg.color_name = returnColourNameDayTime(msg.maxCelcius);
}
else if([16, 17].includes(msg.hour)) {
    msg.color_name = returnColourNameNightTime(msg.minCelcius)
}
else {
    msg.color_name = returnColourNameDayTime(msg.minCelcius)
}


msg.mode = msg.rainPercent > 50 ? "strobe" : "breathe";

function returnColourNameDayTime(tempCelcius) {
    if(tempCelcius >= 25) return "red";
    else if(tempCelcius < 25 && tempCelcius >= 19) return "green";
    else if(tempCelcius < 18) return "teal"
    else return "gold";
}

function returnColourNameNightTime(tempCelcius) {
    if(tempCelcius >= 22) return "red";
    else if(tempCelcius < 22 && tempCelcius >= 17) return "green";
    else if(tempCelcius < 17) return "teal"
    else return "gold";
}

return msg;
